import React from "react";
import { useEffect, useState } from 'react';
import {Link} from "react-router-dom";
import Hamburger from "./Hamburger";


export default function Navbar(){

    function handleScroll() {
        if (window.pageYOffset > 0) {
          document.querySelector('nav').classList.add('black');
        } else {
          document.querySelector('nav').classList.remove('black');
        }
      }

      const [showMenu, setShowMenu] = useState(false);
  
      const handleMenuClick = () => {
        setShowMenu(!showMenu);
      };

      useEffect(() => {                                                         
        window.addEventListener('scroll', handleScroll);
    
        return () => {
          window.removeEventListener('scroll', handleScroll);
        };
      }, []);


    return(
      <>     <Hamburger onClick={handleMenuClick} className={showMenu ? 'active' : ''} />
         <nav onScroll={handleScroll} className={`menu ${showMenu ? 'active' : ''}`}>
           
           <span className="logo">Logo</span>
           <ul>
            <li><Link to="/" className="nav--li">Home</Link></li>
            <li><Link to="/Service" className="nav--li">Service</Link></li>
            <li><Link to="/About" className="nav--li">About</Link></li>
           </ul>
            
        </nav>
        </> 
    )
}
